package cps161_lu5;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for loops: ||initialization|| boolean expression || update||
		for (int i = 1; i <= 10; i++) 
		{
			System.out.println(i);
		}
		
		for (char i = 'a'; i <= 'z'; i++) 
		{
			System.out.println(i);
		}
		// while (boolean expression); DONT USE ;	
	}

}
